    for(var y = 0; y <= height; y += tamanhoDoGrid){
      if ((x+y)%2 ==0) fill(255, transparenciaRegular)
      else fill(0, transparenciaRegular)
      circle(x, y, tamanhoDoGrid)
    }
  }
}

