const CELL_SIZE = 10;
let zSeed = 0;

// Saturation（彩度）
const MAX_SATURATION = 100;
const MIN_SATURATION = 60;
let speedVal_sat = 0;
let addVal_sat = 1;


// Brightness（明度）
const MAX_BRIGHTNESS = 100;
const MIN_BRIGHTNESS = 50;
let speedVal_bright = 0;
let addVal_bright = 1;

